$(window).on("load",function(){const t=$("section");window.onscroll=(()=>{var i="";t.each(function(){const t=this.offsetTop;pageYOffset>=t-60&&(i=this.getAttribute("id"))}),$(".vertical-menu li a").removeClass("active").each(function(){$(this).attr("data-scroll")=="#"+i&&$(this).addClass("active")})})});